  #{spnumber,jdbcType=VARCHAR}, #{linkid,jdbcType=VARCHAR}, #{serviceup,jdbcType=VARCHAR}, #{datetime,jdbcType=VARCHAR}, #{state,jdbcType=VARCHAR}
